style.content:
module.exports = "* {
  box-sizing: border-box;
}
html,
body {
  height: 100%;
}
body {
  margin: 0;
  overflow: hidden;
}
chateau {
  display: flex;
  height: 100%;
  position: relative;
}
chateau > word-area {
  display: block;
  pointer-events: none;
  position: absolute;
  width: 100%;
  height: 100%;
}
chateau > word-area > words {
  border: 1px solid #000;
  border-radius: 4px;
  background-color: #fff;
  padding: 0.5em;
  position: absolute;
  display: inline-block;
}
chateau > canvas {
  flex: 0 0 auto;
  margin: auto;
}
chateau > rooms {
  border-left: 1px solid #808080;
  height: 100%;
  left: 100%;
  padding: 1em;
  position: absolute;
  width: 200px;
}
chateau > rooms.show {
  left: calc(100% - 200px);
}
chateau > rooms > room {
  display: flex;
  margin-bottom: 1em;
  width: 200px;
}
chateau > rooms > room > img {
  border: 1px solid #808080;
  width: 82px;
  height: 47px;
  flex: 0 0 auto;
}
chateau > rooms > room > span.name {
  margin: auto;
}
chateau > form {
  position: absolute;
  bottom: 0;
}
";

