(function() {
  var Core, equals, ok, test;

  Core = require("../core");

  ok = assert;

  equals = assert.equal;

  test = it;

  describe("Core", function() {
    test("#extend", function() {
      var o;
      o = Core();
      o.extend({
        test: "jawsome"
      });
      return equals(o.test, "jawsome");
    });
    test("#attrAccessor", function() {
      var o;
      o = Core({
        test: "my_val"
      });
      o.attrAccessor("test");
      equals(o.test(), "my_val");
      equals(o.test("new_val"), o);
      return equals(o.test(), "new_val");
    });
    test("#attrReader", function() {
      var o;
      o = Core({
        test: "my_val"
      });
      o.attrReader("test");
      equals(o.test(), "my_val");
      equals(o.test("new_val"), "my_val");
      return equals(o.test(), "my_val");
    });
    test("#include", function() {
      var M, o, ret;
      o = Core({
        test: "my_val"
      });
      M = function(I, self) {
        self.attrReader("test");
        return self.extend({
          test2: "cool"
        });
      };
      ret = o.include(M);
      equals(ret, o, "Should return self");
      equals(o.test(), "my_val");
      return equals(o.test2, "cool");
    });
    return test("#include multiple", function() {
      var M, M2, o;
      o = Core({
        test: "my_val"
      });
      M = function(I, self) {
        self.attrReader("test");
        return self.extend({
          test2: "cool"
        });
      };
      M2 = function(I, self) {
        return self.extend({
          test2: "coolio"
        });
      };
      o.include(M, M2);
      return equals(o.test2, "coolio");
    });
  });

}).call(this);

