    });
    describe("#attrObservable", function() {
      it('should allow for observing of attributes', function() {
        var model;
        model = Model({
          name: "Duder"
        });
        model.attrObservable("name");
        model.name("Dudeman");
        return assert.equal(model.name(), "Dudeman");
      });
      return it('should bind properties to observable attributes', function() {
        var model;
        model = Model({
          name: "Duder"
        });
        model.attrObservable("name");
        model.name("Dudeman");
        assert.equal(model.name(), "Dudeman");
        return assert.equal(model.name(), model.I.name);
      });
    });
    describe("#attrModel", function() {
