          people: [
            {
              firstName: "Duder",
              lastName: "Mannington",
              suffix: "Jr."
            }, {
              firstName: "Mr.",
              lastName: "Mannington",
              suffix: "Sr."
            }
          ]
        });
        model.attrModels("people", Person);
