      it("should track pushes", function() {
        var model;
        model = Model({
          people: [
            {
              firstName: "Duder",
              lastName: "Mannington",
              suffix: "Jr."
            }, {
              firstName: "Mr.",
              lastName: "Mannington",
              suffix: "Sr."
            }
