style/window.content:
module.exports = "desktop {
  user-select: none;
  -moz-user-select: none;
  -webkit-user-select: none;
  -ms-user-select: none;
  display: block;
  flex: 1 0 auto;
}
window {
  user-select: none;
  -moz-user-select: none;
  -webkit-user-select: none;
  -ms-user-select: none;
  background-color: #d3d3d3;
  border: 4px double #000;
  display: flex;
  flex-direction: column;
  position: absolute;
}
window > header {
  border-bottom: 1px solid #000;
  cursor: default;
  display: flex;
  flex: 0 0 auto;
}
window > header > close,
window > header > control {
  color: #fff;
  display: inline-block;
  flex: 0 0 auto;
  font-family: monospace;
  width: 18px;
  text-shadow: 1px 0px 0px #000, -1px 0px 0px #000, 0px -1px 0px #000, 0px 1px 0px #000, 1px 1px 0px #808080;
  text-align: center;
}
window > header > title-bar {
  background-color: #000080;
  border-color: #000;
  border-style: solid;
  border-width: 0 1px;
  color: #fff;
  display: inline-block;
  flex: 1 1 auto;
  overflow: hidden;
  padding: 0 2px;
  text-align: center;
  text-overflow: ellipsis;
  white-space: nowrap;
}
window > viewport {
  background-color: #fff;
  display: flex;
  height: 100%;
  overflow: auto;
  position: relative;
  z-index: 0;
}
window > viewport > * {
  margin: auto;
}
window > viewport > textarea {
  font-family: monospace;
  height: 100%;
  resize: none;
  width: 100%;
}
window > viewport > iframe {
  border: none;
  height: 100%;
  width: 100%;
  position: absolute;
}
window > resize {
  display: block;
  position: absolute;
}
window > resize.e,
window > resize.w {
  cursor: ew-resize;
}
window > resize.n,
window > resize.s {
  cursor: ns-resize;
}
window > resize.h {
  height: 4px;
  width: 100%;
}
window > resize.v {
  height: 100%;
  width: 4px;
}
window > resize.w {
  left: -4px;
}
window > resize.e {
  right: -4px;
}
window > resize.n {
  top: -4px;
}
window > resize.s {
  bottom: -4px;
}
window > resize.n.w {
  cursor: nw-resize;
}
window > resize.n.e {
  cursor: ne-resize;
}
window > resize.s.e {
  cursor: se-resize;
}
window > resize.s.w {
  cursor: sw-resize;
}
window > resize.n.v {
  height: 23px;
  border-bottom: 1px solid #000;
}
window > resize.s.v {
  height: 23px;
  border-top: 1px solid #000;
}
window > resize.e.h {
  border-left: 1px solid #000;
  width: 23px;
}
window > resize.w.h {
  border-right: 1px solid #000;
  width: 23px;
}
frame-guard {
  display: block;
  height: 100%;
  pointer-events: none;
  position: absolute;
  width: 100%;
  z-index: 100000;
}
frame-guard.active {
  pointer-events: auto;
}
";

