(function() {
  var Modal;

  PACKAGE.name = "test";

  Modal = require("../main").Modal;

  describe("Modal", function() {
    return it("shoud be totally chill", function() {
      var called, element, handler;
      element = document.createElement("p");
      called = false;
      handler = function(value) {
        called = true;
        return assert.equal(value, "yolo");
      };
      Modal.show(element, handler);
      Modal.hide('yolo');
      return assert(called);
    });
  });

}).call(this);

