(function() {
  module.exports = function(element, handler) {
    var cancel;
    cancel = function(e) {
      e.preventDefault();
      return false;
    };
    element.addEventListener("dragover", cancel);
    element.addEventListener("dragenter", cancel);
    return element.addEventListener("drop", function(e) {
      e.preventDefault();
      handler(e);
      return false;
    });
  };

}).call(this);

