(function() {
  var Drop, rand, sortBy;

  Drop = require("./lib/drop");

  sortBy = function(attribute) {
    return function(a, b) {
      return a[attribute] - b[attribute];
    };
  };

  rand = function(n) {
    return Math.floor(Math.random() * n);
  };

  module.exports = function() {
    var animate, canvas, context, repaint, resize;
    canvas = document.createElement('canvas');
    canvas.width = 960;
    canvas.height = 540;
    context = canvas.getContext('2d');
    repaint = function() {
      var background, indicatorColor, objects;
      context.fillStyle = 'blue';
      context.fillRect(0, 0, canvas.width, canvas.height);
      return;
      background = roomstate.background, objects = roomstate.objects;
      if (background) {
        context.drawImage(background, 0, 0, canvas.width, canvas.height);
      }
      Object.keys(avatars).map(function(accountId) {
        return avatars[accountId];
      }).concat(objects).sort(sortBy("z")).forEach(function(_arg) {
        var color, height, img, width, x, y;
        color = _arg.color, img = _arg.img, x = _arg.x, y = _arg.y;
        if (img) {
          width = img.width, height = img.height;
          return context.drawImage(img, x - width / 2, y - height / 2);
        } else {
          context.fillStyle = color;
          return context.fillRect(x - 25, y - 25, 50, 50);
        }
      });
      if (connected()) {
        indicatorColor = "green";
      } else {
        indicatorColor = "red";
      }
      context.beginPath();
      context.arc(canvas.width - 20, 20, 10, 0, 2 * Math.PI, false);
      context.fillStyle = indicatorColor;
      context.fill();
      context.lineWidth = 2;
      context.strokeStyle = '#003300';
      return context.stroke();
    };
    resize = function() {
      var rect;
      rect = canvas.getBoundingClientRect();
      canvas.width = rect.width;
      return canvas.height = rect.height;
    };
    animate = function() {
      requestAnimationFrame(animate);
      return repaint();
    };
    animate();
    setTimeout(function() {
      return resize();
    });
    return canvas;
  };

}).call(this);

